  description       = "SSH access from allowed CIDR"
  cidr_ipv4         = var.ssh_allowed_cidr
  from_port         = 22
  to_port           = 22
  ip_protocol       = "tcp"

  tags = merge(local.common_tags, {
    Name = "${var.project_name}-ssh-ingress"
  })
}

