{"type":"tool_use","timestamp":1771665073501,"sessionID":"ses_3808902d0ffeug22NKk0Z1MlH7","part":{"id":"prt_c7f77a09a001v5bRWqVB1Zsby3","sessionID":"ses_3808902d0ffeug22NKk0Z1MlH7","messageID":"msg_c7f76fd6e001dbSAbAqMlikIqU","type":"tool","callID":"call_6c117b3613ea412191d05b07","tool":"write","state":{"status":"completed","input":{"content":"# Main Terraform configuration
terraform {
  required_version = ">= 1.5"
  required_providers {
    aws = {
      source  = "hashicorp/aws"
      version = "~> 5.0"
    }
  }
  backend "s3" {
    bucket = "terraform-state-vpc-infrastructure"
    key    = "vpc-infrastructure/terraform.tfstate"
    region = "us-east-1"
  }
}

provider "aws" {
  region = var.aws_region
}

# Variables
variable "aws_region" {
  description = "AWS region for VPC infrastructure"
  type        = string
  default     = "us-east-1"
}

variable "vpc_cidr" {
  description = "VPC CIDR block"
  type        = string
  default     = "10.0.0.0/16"
}

variable "public_subnet_cidrs" {
  description = "Public subnet CIDR blocks"
  type = map(string)
  default = {
    "us-east-1a" = "10.0.1.0/24"
    "us-east-1b" = "10.0.2.0/24"
  }
}

variable "private_subnet_cidrs" {
  description = "Private subnet CIDR blocks"
  type = map(string)
  default = {
    "us-east-1a" = "10.0.3.0/24"
    "us-east-1b" = "10.0.4.0/24"
  }
}

variable "ssh_cidr" {
  description = "CIDR block allowed for SSH access"
  type        = string
  default     = "0.0.0.0/0"
}

variable "project_name" {
  description = "Project name for tagging"
  type        = string
  default     = "vpc-infra"
}

variable "instance_type" {
  description = "EC2 instance type"
  type        = string
  default     = "t3.micro"
}

# Data sources
data "aws_ami" "amazon_linux_2023" {
  most_recent = true
  owners      = ["amazon"]

  filter {
    name   = "name"
    values = ["al2023-ami-2023.*-x86_64"]
  }
}

# VPC
resource "aws_vpc" "main_vpc" {
  cidr_block           = var.vpc_cidr
  enable_dns_hostnames = true
  enable_dns_support   = true

  tags = {
    Name    = "${var.project_name}-vpc"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }
}

# Internet Gateway
resource "aws_internet_gateway" "igw" {
  vpc_id = aws_vpc.main_vpc.id

  tags = {
    Name    = "${var.project_name}-igw"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }
}

# Public subnets
resource "aws_subnet" "public" {
  count                   = 2
  vpc_id                  = aws_vpc.main_vpc.id
  cidr_block              = var.public_subnet_cidrs[keys(var.public_subnet_cidrs)[count.index]]
  availability_zone       = keys(var.public_subnet_cidrs)[count.index]
  map_public_ip_on_launch = true

  tags = {
    Name    = "${var.project_name}-public-${count.index + 1}"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }
}

# Private subnets
resource "aws_subnet" "private" {
  count             = 2
  vpc_id            = aws_vpc.main_vpc.id
  cidr_block        = var.private_subnet_cidrs[keys(var.private_subnet_cidrs)[count.index]]
  availability_zone = keys(var.private_subnet_cidrs)[count.index]

  tags = {
    Name    = "${var.project_name}-private-${count.index + 1}"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }
}

# NAT Gateway
resource "aws_nat_gateway" "nat" {
  allocation_id = aws_eip.nat_eip.id
  subnet_id     = aws_subnet.public[0].id

  tags = {
    Name    = "${var.project_name}-nat-gateway"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }
}

# NAT Gateway EIP
resource "aws_eip" "nat_eip" {
  domain = "vpc"

  tags = {
    Name    = "${var.project_name}-nat-eip"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }
}

# Public route table
resource "aws_route_table" "public" {
  vpc_id = aws_vpc.main_vpc.id

  route {
    cidr_block = "0.0.0.0/0"
    gateway_id = aws_internet_gateway.igw.id
  }

  tags = {
    Name    = "${var.project_name}-public-rt"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }
}

# Private route table
resource "aws_route_table" "private" {
  vpc_id = aws_vpc.main_vpc.id

  route {
    cidr_block     = "0.0.0.0/0"
    nat_gateway_id = aws_nat_gateway.nat.id
  }

  tags = {
    Name    = "${var.project_name}-private-rt"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }
}

# Route table associations
resource "aws_route_table_association" "public" {
  count          = 2
  subnet_id      = aws_subnet.public[count.index].id
  route_table_id = aws_route_table.public.id
}

resource "aws_route_table_association" "private" {
  count          = 2
  subnet_id      = aws_subnet.private[count.index].id
  route_table_id = aws_route_table.private.id
}

# Security group
resource "aws_security_group" "main_sg" {
  name        = "${var.project_name}-security-group"
  description = "Allow SSH and HTTP"
  vpc_id      = aws_vpc.main_vpc.id

  ingress {
    description = "SSH from specific CIDR"
    from_port   = 22
    to_port     = 22
    protocol    = "tcp"
    cidr_blocks = [var.ssh_cidr]
  }

  ingress {
    description = "HTTP from anywhere"
    from_port   = 80
    to_port     = 80
    protocol    = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
  }

  egress {
    from_port   = 0
    to_port     = 0
    protocol    = "-1"
    cidr_blocks = ["0.0.0.0/0"]
  }

  tags = {
    Name    = "${var.project_name}-sg"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }
}

# EC2 instance
resource "aws_instance" "web_server" {
  ami                    = data.aws_ami.amazon_linux_2023.id
  instance_type          = var.instance_type
  subnet_id              = aws_subnet.public[0].id
  vpc_security_group_ids = [aws_security_group.main_sg.id]
  key_name               = var.project_name

  tags = {
    Name    = "${var.project_name}-ec2"
    Environment = "production"
    Project = var.project_name
    ManagedBy = "terraform"
  }

  user_data = <<-EOF
              #!/bin/bash
              yum update -y
              yum install -y httpd
              systemctl start httpd
              systemctl enable httpd
              echo "<h1>${var.project_name} Web Server</h1>" > /var/www/html/index.html
              EOF
}

# Outputs
output "vpc_id" {
  description = "VPC ID"
  value       = aws_vpc.main_vpc.id
}

output "public_subnet_ids" {
  description = "Public subnet IDs"
  value       = aws_subnet.public[*].id
}

output "ec2_public_ip" {
  description = "EC2 instance public IP"
  value       = aws_instance.web_server.public_ip
}

output "nat_gateway_id" {
  description = "NAT Gateway ID"
  value       = aws_nat_gateway.nat.id
}

output "security_group_id" {
  description = "Security Group ID"
  value       = aws_security_group.main_sg.id
}
